import axios from 'axios';

export const receiveUser = (user) => {
    return {
        type: 'RECEIVE_USER',
        user
    };
};

export const FETCH_USER = () => {
    return dispatch =>{
        return axios.get('/auth/getUser')
            .then(response => response.json())
            .then(userJSON => dispatch(receiveUser(userJSON)));
    };
};
