import { combineReducers } from 'redux';

//User Reducers
const userReducer = () =>{
    return {

    };
};

//Events Reducers
const addEventReducer = (oldListOfEvents = [], action) => {
  if(action.type === 'ADD_EVENT'){
      return [...oldListOfEvents, action.payload];
  }
  return oldListOfEvents;
};

//Database Reducers

export default combineReducers({
    user: null,
    events: null,
    databases: null
});
