import { combineReducers } from 'redux';

//User Reducers
const currUser = () =>{
    return {
       type: 'hi'
    };
};

//Events Reducers
const events = (oldListOfEvents = [], action) => {
  if(action.type === 'ADD_EVENT'){
      return [...oldListOfEvents, action.payload];
  }
  return oldListOfEvents;
};

//Database Reducers
export default combineReducers({
    currUser: user,
    events: events,
    databases: null
});
